// Import Libraries
import { Component, OnInit } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
// Import Services
import { SearchService } from '../../services/search.service';
import { SecurityService } from '../../services/security.service';
// Import Models
import { Search } from '../../domain/security_db/search';
import { Security } from '../../domain/security_db/security';

// START - USED SERVICES
/**
* SearchService.create
*	@description CRUD ACTION create
*
* SearchService.update
*	@description CRUD ACTION update
*	@param ObjectId id Id
*
* SearchService.get
*	@description CRUD ACTION get
*	@param ObjectId id Id resource
*
* SecurityService.findBySecurity
*	@description CRUD ACTION findBySecurity
*	@param Objectid key Id of model to search for
*
* SecurityService.list
*	@description CRUD ACTION list
*
*/
// END - USED SERVICES

/**
 * This component allows to edit a Search
 */
@Component({
    selector: 'app-search-edit',
    templateUrl: 'search-edit.component.html',
    styleUrls: ['search-edit.component.css']
})
export class SearchEditComponent implements OnInit {
    item: Search;
    listSearch: Security[];
    externalSecurity_Security: Security[];
    model: Search;
    formValid: Boolean;

    constructor(
    private searchService: SearchService,
    private securityService: SecurityService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new Search();
        this.externalSecurity_Security = [];
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.searchService.get(id).subscribe(item => this.item = item);
                this.securityService.findBySecurity(id).subscribe(list => this.externalSecurity_Security = list);
            }
            // Get relations
            this.securityService.list().subscribe(list => this.listSearch = list);
        });
    }

    /**
     * Check if an Security is in  Search
     *
     * @param {string} id Id of Security to search
     * @returns {boolean} True if it is found
     */
    containSecurity(id: string): boolean {
        if (!this.item.Search) return false;
        return this.item.Search.indexOf(id) !== -1;
    }

    /**
     * Add Security from Search
     *
     * @param {string} id Id of Security to add in this.item.Search array
     */
    addSecurity(id: string) {
        if (!this.item.Search)
            this.item.Search = [];
        this.item.Search.push(id);
    }

    /**
     * Remove an Security from a Search
     *
     * @param {number} index Index of Security in this.item.Search array
     */
    removeSecurity(index: number) {
        this.item.Search.splice(index, 1);
    }

    /**
     * Save Search
     *
     * @param {boolean} formValid Form validity check
     * @param Search item Search to save
     */
    save(formValid: boolean, item: Search): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.searchService.update(item).subscribe(data => this.goBack());
            } else {
                this.searchService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



